mapper.select:
/* getListConverterJobTotalCount */
SELECT COUNT(converter_job_id) 
FROM converter_job
WHERE user_id = #{user_id}

	AND ${search_word} = #{search_value}


	AND ${search_word} LIKE '%' || #{search_value} || '%'


	AND status = #{status}



	AND insert_date >= TO_TIMESTAMP(#{start_date}, 'YYYYMMDDHH24MISSUS')




	AND insert_date <= TO_TIMESTAMP(#{end_date}, 'YYYYMMDDHH24MISSUS')

/* getListConverterJob */
SELECT X.*, 
	(SELECT COUNT(converter_job_file_id) FROM converter_job_file WHERE converter_job_id = X.converter_job_id) AS converter_file_count
FROM (
	SELECT A.*
	FROM converter_job A
	WHERE A.user_id = #{user_id}

		AND A.${search_word} = #{search_value}


		AND A.${search_word} LIKE '%' || #{search_value} || '%'


		AND A.status = #{status}



		AND A.insert_date >= TO_TIMESTAMP(#{start_date}, 'YYYYMMDDHH24MISSUS')




		AND A.insert_date <= TO_TIMESTAMP(#{end_date}, 'YYYYMMDDHH24MISSUS')




	ORDER BY A.${order_word} ${order_value}


	ORDER BY A.insert_date DESC, A.converter_job_id DESC


	OFFSET #{offset} LIMIT #{limit}
) X

mapper.insert:
		/* insertConverterJob */

    		SELECT NEXTVAL('converter_job_seq')

		INSERT INTO converter_job(
			converter_job_id, upload_data_id, user_id, title, converter_type, file_count,

			status, 

			error_code
		) VALUES (
			#{converter_job_id}, #{upload_data_id}, #{user_id}, #{title}, #{converter_type}, #{file_count},

			#{status}, 

			#{error_code}
		)

		/* insertConverterJobFile */

    		SELECT NEXTVAL('converter_job_file_seq')

		INSERT INTO converter_job_file(
			converter_job_file_id, converter_job_id, upload_data_id, user_id,

			status, 

			error_code
		) VALUES (
			#{converter_job_file_id}, #{converter_job_id}, #{upload_data_id}, #{user_id},

			#{status}, 

			#{error_code}
		)

mapper.update:
/* updateConverterJob */
UPDATE converter_job
SET 

	title = #{title},


	status = #{status},


	converter_type = #{converter_type},


	error_code = #{error_code},

	update_date = now()
WHERE converter_job_id = #{converter_job_id}

	AND user_id = #{user_id}
